sourcesContent:
import express from "express";
import Review from "../models/Review.js";
import multer from "multer";

// Initialize router
const router = express.Router();

// Configure multer for file uploads
const storage = multer.memoryStorage(); // Store files in memory
const upload = multer({ storage: storage });

// Create a new review
router.post("/", upload.array("images", 10), async (req, res) => {
  const { title, category, brand, description, author } = req.body;
  const images = req.files.map((file) => file.buffer); // Convert files to Buffer

  try {
    const newReview = new Review({
      title,
      category,
      brand,
      images,
      description,
      author,
    });

    const review = await newReview.save();
    res.status(201).json(review);
  } catch (err) {
    console.error(err.message);
    res.status(500).send("Server error");
  }
});

// Get all reviews
router.get("/", async (req, res) => {
  try {
    const reviews = await Review.find();
    res.json(reviews);
  } catch (err) {
    console.error(err.message);
    res.status(500).send("Server error");
  }
});

// Get a review by ID
router.get("/:id", async (req, res) => {
  try {
    const review = await Review.findById(req.params.id);

    if (!review) {
      return res.status(404).json({ msg: "Review not found" });
    }

    res.json(review);
  } catch (err) {
    console.error(err.message);
    res.status(500).send("Server error");
  }
});

// Update a review
router.put("/:id", upload.array("images", 10), async (req, res) => {
  const { title, category, brand, description, author } = req.body;
  const images = req.files.map((file) => file.buffer); // Convert files to Buffer

  try {
    const review = await Review.findById(req.params.id);

    if (!review) {
      return res.status(404).json({ msg: "Review not found" });
    }

    review.title = title || review.title;
    review.category = category || review.category;
    review.brand = brand || review.brand;
    review.images = images.length ? images : review.images; // Update images if provided
    review.description = description || review.description;
    review.author = author || review.author;

    await review.save();
    res.json(review);
  } catch (err) {
    console.error(err.message);
    res.status(500).send("Server error");
  }
});

// Delete a review
router.delete("/:id", async (req, res) => {
  try {
    console.log(`Delete request received for ID: ${req.params.id}`);
    const review = await Review.findById(req.params.id);

    if (!review) {
      return res.status(404).json({ msg: "Review not found" });
    }

    await review.remove();
    console.log(`Review with ID ${req.params.id} removed successfully.`);
    res.json({ msg: "Review removed" });
  } catch (err) {
    console.error("Delete error:", err.message);
    res.status(500).send("Server error");
  }
});

// Export the router
export default router;

